String encry(String pa,String adp,int  n){
int arr[]=new int[20];
        int ar2[]=new int[20];double p[]=new double[20];
  int ar3[]=new int[20];      int ps[]=new int[20]; 
 try{
     for(int i=0;i<20;i++)
  {arr[i]=0;ar2[i]=0;ar3[i]=0;p[i]=0;
  }
for(int i=0;i<pa.length();i++)
{if(i<pa.length())
 arr[i]=(int)pa.charAt(i);else arr[i]=0;
if(i<adp.length())
    ar2[i]=(int)adp.charAt(i);
else
    ar2[i]=0;
}
    
    for (int i=0;i<pa.length();i++)
    {
            ar3[i]=arr[i]+ar2[i];

        String pp1="";
    int j=0,maxt=0;double sp=ar3[i];
    
 for(int t=1;sp>=Math.pow(n,t);t++)
 maxt=t;
        System.out.println("maxt"+maxt);             
do
{p[j]=(sp-(sp%Math.pow(n,maxt-j)))/Math.pow(n,maxt-j);
sp=sp%Math.pow(n,maxt-j);
pp1=pp1+(int)p[j];j++;
}while(j<=maxt);
 ps[i]=Integer.parseInt(pp1);
 System.out.println(ps[i]+"   ,"+"/n"+ar3[i]);
    } }catch(Exception e)
            {System.out.println(e.getMessage());}
 String p3="";
  for (int l=0;l<pa.length();l++)
      p3+=ps[l]+",";
  return(p3);
}
