if(in == JOptionPane.OK_OPTION){
 try{
 
    sql = "select * from user where AccNo = '"+accno2+"';";
    rs = st.executeQuery(sql);
    rs.first();
    
    int bal1 = rs.getInt(3);
    int newbal = bal1 - amt;
    
    sql = "Update user set Balance = '"+newbal+"' where AccNo = '"+accno2+"';";
    st.executeUpdate(sql);
    
    sql = "select * from user where AccNo = '"+acct+"';";
    rs = st.executeQuery(sql);
    rs.first();
    
    payee = rs.getString(2);
    int bal2 = rs.getInt(3);
    int newbal2 = bal2 + amt;
    
    sql = "Update user set Balance = '"+newbal2+"' where AccNo = '"+acct+"';";
    st.executeUpdate(sql);
    
       JOptionPane.showMessageDialog(rootPane,"Transaction Successful");
    
    sql = "insert into trhis values('"+payer+"','"+payee+"','"+amt+"',curdate(),curtime());";
    st.executeUpdate(sql);
  }
 
 catch(Exception e){
    JOptionPane.showMessageDialog(rootPane,"Invalid Details. "+"\n"+e.getMessage());
    }
}

else if(in == JOptionPane.CANCEL_OPTION){
JOptionPane.showMessageDialog(rootPane, "Transaction Aborted");
}
